import React, { useState, useContext } from "react";
import { GlobalState } from "../../GlobalState";
import Menu from "./menu.svg";
import Close from "./close.svg";
import Cart from "./cart.svg";
import User from "./user.svg";
import { Link } from "react-router-dom";
import axios from "axios";
export default function Header() {
  const state = useContext(GlobalState);
  const [isLogged] = state.userAPI.isLogged;
  const [isAdmin] = state.userAPI.isAdmin;
  const [isSeller] = state.userAPI.isSeller;
  const [cart] = state.userAPI.cart;
  const [search, setSearch] = state.productsAPI.search;
  const logOut = async () => {
    await axios.get("/user/logout");
    localStorage.clear();
    window.location.href = "/";
  };

  const [infor, setInfor] = state.userAPI.infor;
  const removeAccent = (str) => {
    str = str.replace(/à|á|ạ|ả|ã|â|ầ|ấ|ậ|ẩ|ẫ|ă|ằ|ắ|ặ|ẳ|ẵ/g, "a");
    str = str.replace(/è|é|ẹ|ẻ|ẽ|ê|ề|ế|ệ|ể|ễ/g, "e");
    str = str.replace(/ì|í|ị|ỉ|ĩ/g, "i");
    str = str.replace(/ò|ó|ọ|ỏ|õ|ô|ồ|ố|ộ|ổ|ỗ|ơ|ờ|ớ|ợ|ở|ỡ/g, "o");
    str = str.replace(/ù|ú|ụ|ủ|ũ|ư|ừ|ứ|ự|ử|ữ/g, "u");
    str = str.replace(/ỳ|ý|ỵ|ỷ|ỹ/g, "y");
    str = str.replace(/đ/g, "d");
    str = str.replace(/À|Á|Ạ|Ả|Ã|Â|Ầ|Ấ|Ậ|Ẩ|Ẫ|Ă|Ằ|Ắ|Ặ|Ẳ|Ẵ/g, "A");
    str = str.replace(/È|É|Ẹ|Ẻ|Ẽ|Ê|Ề|Ế|Ệ|Ể|Ễ/g, "E");
    str = str.replace(/Ì|Í|Ị|Ỉ|Ĩ/g, "I");
    str = str.replace(/Ò|Ó|Ọ|Ỏ|Õ|Ô|Ồ|Ố|Ộ|Ổ|Ỗ|Ơ|Ờ|Ớ|Ợ|Ở|Ỡ/g, "O");
    str = str.replace(/Ù|Ú|Ụ|Ủ|Ũ|Ư|Ừ|Ứ|Ự|Ử|Ữ/g, "U");
    str = str.replace(/Ỳ|Ý|Ỵ|Ỷ|Ỹ/g, "Y");
    str = str.replace(/Đ/g, "D");
    return str.toLowerCase().trim().split(/\s+/).join('+');
}
  const loggedControl = () => {
    return (
      <>
        <li>
          <div className="usericon">
            <div className="icon">
              <img src={User} alt="" width="30" />
              <p>{infor.name}</p>
            </div>
            <div className="dropdown-menu">
              <li>
                <Link to="/">
                  {isAdmin
                    ? adminControl2()
                    : isSeller
                    ? sellerControl2()
                    : userControl()}
                </Link>
              </li>
              <li>{!isAdmin ? <Link to="/account">Your account</Link> : ""}</li>
              <li>
                {!isAdmin ? (
                  <Link to="/account/history">History Order</Link>
                ) : (
                  ""
                )}
              </li>
              <li>
                <Link to="/" onClick={logOut}>
                  Logout
                </Link>
              </li>
            </div>
          </div>
        </li>
      </>
    );
  };

  const userControl = () => {
    return (
      <>
        <li>
          <Link to="/cart">Your Cart</Link>
        </li>
        
      </>
    );
  };

  const sellerControl2 = () => {
    return (
      <>
        <li>
          <Link to="/product-management">Shop management</Link>
        </li>
      </>
    );
  };

  const sellerControl = () => {
    return (
      <>
        <li>
          <Link to="/product-management">Your shop</Link>
        </li>
      </>
    );
  };
  /*create product nam trong product management */

  const adminControl = () => {
    return (
      <>
        <li>
          <Link to="/user-management">Data management</Link>
        </li>
      </>
    );
  };

  const adminControl2 = () => {
    return (
      <>
        <li>
          <Link to="/user-management">User Management</Link>
        </li>
        <li>
          <Link to="/product-management">Product Management</Link>
        </li>
      </>
    );
  };

  const Search = (e) => {};

  return (
    //FE (Thu)
    <header>
      <div className="menu">
        <img src={Menu} alt="" width="30" />
      </div>
      <div className="logo">
        <h1>
          <Link to="/">
            {isAdmin ? "Admin" : isSeller ? "Seller" : "rookieSE"}
          </Link>
        </h1>
      </div>
      <div class="search-box">
        <form onSubmit={Search} action={'/search' + removeAccent(search)}>
          <input
            type="text"
            name=""
            placeholder="Search..."
            className="search-field"
            value={search}
            onChange={(e) => setSearch(e.target.value.toLowerCase())}
          />
        </form>
      </div>
      <ul>
        <li>
          <Link to="/">
            {isAdmin ? adminControl() : isSeller ? sellerControl() : "Product"}
          </Link>
        </li>
        {isLogged ? (
          loggedControl()
        ) : (
          <li>
            <Link to="/login">Login & Register</Link>
          </li>
        )}
        <li>
          <img src={Close} alt="" width="30" className="menu" />
          <div className="cart-icon">
            <span>{cart.length}</span>
            <Link to="/cart">
              <img src={Cart} alt="" width="30" />
            </Link>
          </div>
        </li>
      </ul>
    </header>
  );
}
